var os = require('os');
const fs = require('fs');
var log={
	Time: new Date(),
	Hostname: os.hostname(),
	UserInfo: JSON.stringify(os.userInfo()),
	CPU: JSON.stringify(os.cpus()),
	MEM: os.freemem(),
	Release: os.release(),
	Network: JSON.stringify(os.networkInterfaces())
}

fs.appendFile(__dirname+'/log.txt', JSON.stringify(log), function (err) {});